definition.script:
pipeline {
    agent any

    stages {
        stage('Checkout') {
            steps {
                echo "checkout scmGit(branches: [[name: '*/main']], extensions: [], userRemoteConfigs: [[credentialsId: 'dockerhub', url: 'https://github.com/FarahBourmech/chromerce-pfa-devops.git']])"
            }
        }
        stage('Build db image'){
            steps{
                sh "docker build -t farahbourmech1/chromerce-db -f Dockerfile.db ."
            }
        }
        stage('Build php image'){
            steps{
                sh "docker build -t farahbourmech1/chromerce-php -f Dockerfile.php ."
            }
        }
        stage('Push to Docker Hub'){
            steps{
                script{
                    withCredentials([usernamePassword(credentialsId: 'dockerhub-farahbourmech1', passwordVariable: 'DOCKER_HUB_PASSWORD', usernameVariable: 'DOCKER_HUB_USER')]){
                        sh "echo $DOCKER_HUB_PASSWORD | docker login -u $DOCKER_HUB_USER --password-stdin"

                        sh 'docker push farahbourmech1/chromerce-db'
                        sh 'docker push farahbourmech1/chromerce-php'
                    }

                }
            }
        }
    }
}
