definition.script:
pipeline {
    agent any

    stages {
        stage('Checkout') {
            steps {
                echo "checkout scmGit(branches: [[name: '*/main']], extensions: [], userRemoteConfigs: [[credentialsId: 'dockerhub', url: 'https://github.com/FarahBourmech/chromerce-pfa-devops.git']])"
            }
        }
        stage('Build DB image') {
            steps {
                sh 'sudo docker build -t farahbourmech1/chromerce-db -f /var/jenkins_home/Dockerfile.db .'
            }
        }
        stage('Build php image') {
            steps {
                sh 'sudo docker build -t farahbourmech1/chromerce-php -f /var/jenkins_home/Dockerfile.php .'
            }
        }


    }


    }
